INSERT INTO Genres( Name, Description) VALUES (1, 'Fantasy', 'A genre that incorporates imaginative and fantastic themes. These themes usually involve magic, supernatural events, or fantasy worlds.');
INSERT INTO Genres(Name, Description) VALUES (2, 'Romance', 'A genre wherein the plot revolves around the love between two protagonists. This genre usually has a theme that explores an issue within love, including but not limited to: love at first sight, forbidden love, love triangles, and sacrificial love.');
INSERT INTO Genres(Name, Description) VALUES (3, 'Science-Fiction', 'A genre that incorporates hypothetical, science-based themes into the plot of the film. Often, this genre incorporates futuristic elements and technologies to explore social, political, and philosophical issues.');
INSERT INTO Genres(Name, Description) VALUES (4, 'Mystery', 'Centers on a person of authority, usually a detective, that is trying to solve a mysterious crime.');
INSERT INTO Genres(Name, Description) VALUES (5, 'Drama', 'A genre that relies on the emotional and relational development of realistic characters. While Drama film relies heavily on this kind of development, dramatic themes play a large role in the plot as well.');
INSERT INTO Genres(Name, Description) VALUES (6, 'Adventure', 'Adventure film is a genre that revolves around the conquests and explorations of a protagonist. The purpose of the conquest can be to retrieve a person or treasure, but often the main focus is simply the pursuit of the unknown.');
INSERT INTO Genres(Name, Description) VALUES (7, 'Family', 'A genre that is contains appropriate content for younger viewers. Family film aims to appeal not only to children, but to a wide range of ages. While the storyline may appeal to a younger audience, there are components of the film that are geared towards adults- such as witty jokes and humor.');
