INSERT INTO Movies(Title, Description,  directorid,imageurl,featured) VALUES (1, 'My Neighbor Totoro', 'When two girls move to the country to be near their ailing mother, they have adventures with the wondrous forest spirits who live nearby.', 1, 1, NULL, true);
INSERT INTO Movies(Title, Description,  directorid,imageurl,featured) VALUES (2, 'Amelie', 'Amélie is an innocent and naive girl in Paris with her own sense of justice. She decides to help those around her and, along the way, discovers love.', 2, 2, NULL, false);
INSERT INTO Movies(Title, Description,  directorid,imageurl,featured) VALUES (3, 'Inception', 'A thief who steals corporate secrets through the use of dream-sharing technology is given the inverse task of planting an idea into the mind of a C.E.O., but his tragic past may doom the project and his team to disaster.', 3, 3, NULL, false);
INSERT INTO Movies(Title, Description,  directorid,imageurl,featured) VALUES (4, 'Knives Out', 'A detective investigates the death of the patriarch of an eccentric, combative family.', 4, 4, NULL, false);
INSERT INTO Movies(Title, Description,  directorid,imageurl,featured) VALUES (5, 'Little Women', 'Jo March reflects back and forth on her life, telling the beloved story of the March sisters - four young women, each determined to live life on her own terms.', 5, 5, NULL, false);
INSERT INTO Movies(Title, Description,  directorid,imageurl,featured) VALUES (6, 'Moana', 'In Ancient Polynesia, when a terrible curse incurred by the Demigod Maui reaches Moana''s island, she answers the Ocean''s call to seek out the Demigod to set things right.', 6, 6, NULL, false);
INSERT INTO Movies(Title, Description,  directorid,imageurl,featured) VALUES (7, 'Pride & Prejudice', 'Sparks fly when spirited Elizabeth Bennet meets single, rich, and proud Mr. Darcy. But Mr. Darcy reluctantly finds himself falling in love with a woman beneath his class. Can each overcome their own pride and prejudice?', 5, 7, NULL, false);
INSERT INTO Movies(Title, Description,  directorid,imageurl,featured) VALUES (8, 'Tangled', 'The magically long-haired Rapunzel has spent her entire life in a tower, but now that a runaway thief has stumbled upon her, she is about to discover the world for the first time, and who she really is.', 8, 8, NULL, false);
INSERT INTO Movies(Title, Description,  directorid,imageurl,featured) VALUES (9, '(500) Days of Summer', 'After being dumped by the girl he believes to be his soulmate, hopeless romantic Tom Hansen reflects on their relationship to try and figure out where things went wrong and how he can win her back.', 2, 9, NULL, false);
INSERT INTO Movies(Title, Description,  directorid,imageurl,featured) VALUES (10, 'Spirited Away', 'During her family''s move to the suburbs, a sullen 10-year-old girl wanders into a world ruled by gods, witches, and spirits, and where humans are changed into beasts.', 1, 2, NULL, false);
